import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardActions from "@material-ui/core/CardActions";
import CardContent from "@material-ui/core/CardContent";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";


const useStyles = makeStyles((theme) => ({
  root: {
    minWidth: 275,
    maxWidth: 290,
    cursor: "pointer",
    marginTop: 20,
    borderRadius: 12,
    boxShadow: "0 10px 45px rgba(0,0,0,.08), 0 5px 10px rgba(0,0,0,.05)",
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: "1px solid #0020",
    boxShadow: theme.shadows[5],
    outline: "none",
    padding: theme.spacing(13, 13, 13),
  },
}));

export default function CityCard({ cityWeather, temp, sky, icon }) {
  const classes = useStyles();
  const bull = <span className={classes.bullet}>•</span>;

  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const cels = Math.round(temp - 273.16);

  return (
    <React.Fragment>
      <Card className={classes.root} onClick={handleOpen}>
        <CardContent>
          <Typography
            className={classes.title}
            color="textSecondary"
            gutterBottom
          >
            Current weather in
          </Typography>
          <Typography variant="h5" component="h2">
            {cityWeather.name}
          </Typography>
          <img
            src={`http://openweathermap.org/img/w/${icon}.png`}
            alt="icon"
            width="60"
            height="50"
          />
          <Typography>Temperature: {cels}℃</Typography>
          <Typography>Description: {sky}</Typography>
        </CardContent>
      </Card>
      <Modal
        aria-labelledby="transition-modal-title"
        aria-describedby="transition-modal-description"
        className={classes.modal}
        open={open}
        onClose={handleClose}
        closeAfterTransition
        BackdropComponent={Backdrop}
        BackdropProps={{
          timeout: 500,
        }}
      >
        <Fade in={open}>
          <div className={classes.paper}>
            <Typography
              className={classes.title}
              color="textSecondary"
              gutterBottom
            >
              more weather information
            </Typography>
            <Typography variant="h5" component="h2">
              {cityWeather.name}
            </Typography>
            <img
              src={`http://openweathermap.org/img/w/${icon}.png`}
              alt="icon"
              width="60"
              height="50"
            />
            <Typography>Temperature: {cels}℃</Typography>
            <Typography>Description: {sky}</Typography>
          </div>
        </Fade>
      </Modal>
    </React.Fragment>
  );
}
