import React, { useState, useEffect } from "react";
import "./App.css";
import HideAppBar from "./components/AppBar";
import CityCard from "./components/CityCard";
import Grid from "@material-ui/core/Grid";

function App() {
  const [moscow, setMoscow] = useState({
    datasm: [],
  });

  const { datasm } = moscow;

  const [london, setLondon] = useState({
    datasl: [],
  });

  const { datasl } = london;

  const [paris, setParis] = useState({
    datasp: [],
    temp: [],
    sky: [],
    icon: "",
  });

  const { datasp } = paris;

  useEffect(() => {
    getWeatherParis();
    getWeatherMoscow();
    getWeatherLondon();
  }, []);

  const getWeatherParis = async () => {
    const api = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=paris&appid=62029f0d3701271a5e50142b280915f0
`
    );
    const data = await api.json();
    setParis({
      ...paris,
      datasp: data,
      temp: data.main.temp,
      sky: data.weather[0].description,
      icon: data.weather[0].icon,
    });

    console.log(data);
  };
  const getWeatherMoscow = async () => {
    const api = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=moscow&appid=62029f0d3701271a5e50142b280915f0
`
    );
    const data = await api.json();
    setMoscow({
      ...moscow,
      datasm: data,
      temp: data.main.temp,
      sky: data.weather[0].description,
      icon: data.weather[0].icon,
    });
    console.log(data);
  };
  const getWeatherLondon = async () => {
    const api = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=london&appid=62029f0d3701271a5e50142b280915f0
`
    );
    const data = await api.json();
    setLondon({
      ...london,
      datasl: data,
      temp: data.main.temp,
      sky: data.weather[0].description,
      icon: data.weather[0].icon,
    });
    console.log(data);
  };

  return (
    <div className="App">
      <header className="App-header">
        <HideAppBar />
      </header>
      <div className="Layout">
        <Grid
          container
          direction="row"
          justify="center"
          alignItems="center"
          spacing={2}
        >
          <Grid item xs={3}>
            <CityCard
              cityWeather={paris.datasp}
              temp={paris.temp}
              sky={paris.sky}
              icon={paris.icon}
            />
          </Grid>
          <Grid item xs={3}>
            <CityCard
              cityWeather={moscow.datasp}
              temp={moscow.temp}
              sky={moscow.sky}
              icon={moscow.icon}
            />
          </Grid>
          <Grid item xs={3}>
            <CityCard
              cityWeather={moscow.datasp}
              temp={moscow.temp}
              sky={moscow.sky}
              icon={moscow.icon}
            />
          </Grid>
        </Grid>
      </div>
    </div>
  );
}

export default App;

