import React, { useState, useEffect } from "react";
import "./App.css";
import HideAppBar from "./components/AppBar";
import CityCard from "./components/CityCard";
import Grid from "@material-ui/core/Grid";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import CardContent from "@material-ui/core/CardContent";
import Typography from "@material-ui/core/Typography";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";

const useStyles = makeStyles((theme) => ({
  root: {
    minWidth: 275,
    maxWidth: 290,
    cursor: "pointer",
    marginTop: 20,
    borderRadius: 12,
    boxShadow: "0 10px 45px rgba(0,0,0,.08), 0 5px 10px rgba(0,0,0,.05)",
  },
  bullet: {
    display: "inline-block",
    margin: "0 2px",
    transform: "scale(0.8)",
  },
  title: {
    fontSize: 14,
  },
  pos: {
    marginBottom: 12,
  },
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    border: "1px solid #48484A",
    borderRadius: 4,
    maxWidth: 1000,
    boxShadow: theme.shadows[5],
    outline: "none",
  },
  modalCard: {
    width: 350,
    boxShadow: "0 10px 45px rgba(0,0,0,.08), 0 5px 10px rgba(0,0,0,.05)",
  },
}));

function App() {
  const classes = useStyles();

  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const cels = Math.round(temp - 273.16);

  const timeCity = (times) => {
    let a = new Date(times * 1000);
    let months = [
      "Jan",
      "Feb",
      "Mar",
      "Apr",
      "May",
      "Jun",
      "Jul",
      "Aug",
      "Sep",
      "Oct",
      "Nov",
      "Dec",
    ];
    let year = a.getFullYear();
    let month = months[a.getMonth()];
    let date = a.getDate();
    let hour = a.getHours();
    let min = a.getMinutes();
    let sec = a.getSeconds();
    let time =
      date + " " + month + " " + year + " " + hour + ":" + min + ":" + sec;
    return time;
  };

  const [moscow, setMoscow] = useState({
    datasm: [],
  });

  const { datasm } = moscow;

  const [london, setLondon] = useState({
    datasl: [],
  });

  const { datasl } = london;

  const [paris, setParis] = useState({
    datasp: [],
    datafp: [],
    temp: [],
    sky: [],
    icon: "",
    time: "",
  });

  const { datasp, datafp, temp, sky, icon, time } = paris;

  useEffect(() => {
    getWeatherParis();
    getWeatherMoscow();
    getWeatherLondon();
  }, []);

  const getWeatherMoscow = async () => {
    const api = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=moscow&appid=62029f0d3701271a5e50142b280915f0
`
    );
    const forecastapi = await fetch(
      `https://api.openweathermap.org/data/2.5/forecast?q=moscow&units=metric&appid=62029f0d3701271a5e50142b280915f0`
    );

    const datafore = await forecastapi.json();
    const data = await api.json();
    setMoscow({
      ...moscow,
      datasm: data,
      datafm: datafore,
      temp: data.main.temp,
      sky: data.weather[0].description,
      icon: data.weather[0].icon,
    });
    console.log(data);
  };
  const getWeatherLondon = async () => {
    const api = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=london&appid=62029f0d3701271a5e50142b280915f0
`
    );
    const forecastapi = await fetch(
      `https://api.openweathermap.org/data/2.5/forecast?q=moscow&units=metric&appid=62029f0d3701271a5e50142b280915f0`
    );
    const data = await api.json();
    const datafore = await forecastapi.json();

    setLondon({
      ...london,
      datasl: data,
      datafl: datafore.list.slice(0, 5).map((item) => <a>{item.main.temp}</a>),
      temp: data.main.temp,
      sky: data.weather[0].description,
      icon: data.weather[0].icon,
    });
    console.log(data);
  };

  const getWeatherParis = async () => {
    const api = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?q=sydney&appid=62029f0d3701271a5e50142b280915f0
`
    );
    const forecastapi = await fetch(
      `https://api.openweathermap.org/data/2.5/forecast?q=moscow&units=metric&appid=62029f0d3701271a5e50142b280915f0`
    );

    const data = await api.json();
    const datafore = await forecastapi.json();
    setParis({
      ...paris,
      datasp: data,
      datafp: datafore.list[0].dt,
      temp: data.main.temp,
      sky: data.weather[0].description,
      icon: data.weather[0].icon,
      time: data.dt,
    });

    console.log(data);
  };

  return (
    <div className="App">
      <header className="App-header">
        <HideAppBar />
      </header>

      <div className="Layout">
        <Grid
          container
          direction="row"
          justify="center"
          alignItems="center"
          spacing={7}
        >
          <Grid item>
            <CityCard
              cityWeather={paris.datasp}
              cityForeWeather={paris.datafp}
              temp={paris.temp}
              sky={paris.sky}
              icon={paris.icon}
              time={paris.time}
            />
          </Grid>
          <Grid item>
            <CityCard
              cityWeather={moscow.datasm}
              temp={moscow.temp}
              sky={moscow.sky}
              icon={moscow.icon}
            />
          </Grid>
          <Grid item>
            <CityCard
              cityWeather={london.datasl}
              temp={london.temp}
              sky={london.sky}
              icon={london.icon}
            />
          </Grid>
        </Grid>
      </div>
    </div>
  );
}

export default App;

