import React, { useState, useEffect } from "react";
import "./App.css";
import HideAppBar from "./components/AppBar";
import RecipeReviewCard from "./components/CityCard";
import axios from "axios";
import Weather from './components/Weather'


function App() {

  const [data, setData] = useState({
    weather: [],
    temp: [],
    clouds: [],
  });

  useEffect(() => {
    getWeather()
  }, []);

  const getWeather = () => {
    axios.get(
        `https://api.openweathermap.org/data/2.5/find?q=moscow&units=imperial&appid=f92c1f4990b0574d4a4e4d3dd556f388`
      )
      .then((response) => {
        this.setState({
          weather: response.data.list[0],
          temp: response.data.list[0].main.temp,
          clouds: response.data.list[0].weather[0].description,
        });
      })
      .catch((error) => {
        console.log("Error", error);
      });
  };


  return (
    <div className="App">
      <header className="App-header">
        <HideAppBar />
        <Weather
          city={data.weather.name}
          temp={data.temp}
          clouds={data.clouds}
        />
      </header>
    </div>
  );
}

export default App;

